output "lambda_name" {
  value = aws_lambda_function.function.function_name
}

output "lambda_arn" {
  value = aws_lambda_function.function.arn
}

output "lambda_role_name" {
  value = aws_iam_role.lambda.name
}

output "lambda_role_arn" {
  value = aws_iam_role.lambda.arn
}

output "lambda_role_id" {
  value = aws_iam_role.lambda.id
}


output "lambda_id" {
  value = aws_lambda_function.function.id
}

output "lambda_invoke_arn" {
  value = aws_lambda_function.function.invoke_arn
}

output "lambda_handler" {
  value = aws_lambda_function.function.handler
}

output "lambda_filename" {
  value = aws_lambda_function.function.filename
}

output "lambda_security_group_name" {
  value = var.create_security_group ? element(aws_security_group.lambda_security_group[*].name, 0) : null
}

output "lambda_security_group_arn" {
  value = var.create_security_group ? element(aws_security_group.lambda_security_group[*].arn, 0) : null
}

output "lambda_security_group_id" {
  value = var.create_security_group ? element(aws_security_group.lambda_security_group[*].id, 0) : null
}


output "lambda_security_group_ingress" {
  value = var.create_security_group ? element(aws_security_group.lambda_security_group[*].ingress, 0) : null
}

output "lambda_security_group_egress" {
  value = var.create_security_group ? element(aws_security_group.lambda_security_group[*].egress, 0) : null
}

output "lambda_log_group" {
  value = var.create_cloud_watch_log_group ? element(aws_cloudwatch_log_group.lambda_log_group[*].name, 0) : null
}

output "lambda_invocation_image_uri" {
  value = aws_lambda_function.function.image_uri
}

output "addtional_security_group" {
  value = var.addtional_security_group
}

resource "aws_lambda_function" "function" {
  function_name    = var.function_name
  role             = aws_iam_role.lambda.arn
  runtime          = var.runtime
  filename         = var.deployment_file
  handler          = var.handler
  timeout          = var.function_timeout
  architectures    = [var.lambda_architecture]
  source_code_hash = data.archive_file.lambda_payload.output_base64sha256
  publish          = var.publish
  memory_size      = 512
  logging_config {
    application_log_level = "INFO"
    log_format            = "JSON"
    system_log_level      = "INFO"
    log_group             = aws_cloudwatch_log_group.lambda_log_group[0].name
  }
  environment {
    variables = {
      S3_BUCKET                = var.s3_bucket_result,
      INTERNAL_BASE_URL        = var.internal_base_url,
      MAX_RETRY_COUNT          = var.max_retry_count,
      SQS_MESSAGE_QUEUE_URL    = var.sqs_message_queue_url
      SECRETSMANAGER_PLAID_API = var.SECRETSMANAGER_PLAID_API
      WEBHOOK_LAMBDA           = var.WEBHOOK_LAMBDA
      IVAAS_SUBGRAPH_URL       = var.IVAAS_SUBGRAPH_URL
      NODE_EXTRA_CA_CERTS      = var.NODE_EXTRA_CA_CERTS
      PLAID_ENV                = var.PLAID_ENV
    }
  }
  vpc_config {
    subnet_ids         = var.subnet_ids
    security_group_ids = [aws_security_group.lambda_security_group[0].id, var.addtional_security_group]
  }

  depends_on = [
    data.archive_file.lambda_payload,
    aws_cloudwatch_log_group.lambda_log_group,
    aws_iam_role.lambda,
  ]

  #   lifecycle {
  #   ignore_changes                = [vpc_config]
  # }
}

data "aws_caller_identity" "lambda" {}
data "aws_region" "lambda" {}

data "archive_file" "lambda_payload" {
  type       = "zip"
  source_dir = "${var.src}"
  output_path = var.zip_file_name
}


data "aws_iam_policy_document" "lambda" {
  count = var.create_cloud_watch_log_group != null ? 1 : 0
  statement {
    # sid = "writeLogs"
    actions = [
      "logs:CreateLogGroup",
      "logs:CreateLogStream",
      "logs:PutLogEvents",
    ]
    effect    = "Allow"
    resources = ["*"]
    # resources = [
    #   "${aws_cloudwatch_log_group.lambda_log_group[0].arn}:*"
    # ]
  }
  statement {
    actions = [
      "ec2:CreateNetworkInterface",
      "ec2:DescribeNetworkInterfaces",
      "ec2:DeleteNetworkInterface",
      "ec2:DetachNetworkInterface",
      "ec2:DescribeSecurityGroups", // Add this line
      "ec2:ModifyNetworkInterfaceAttribute"
    ]
    effect    = "Allow"
    resources = ["*"]
  }

  statement {
    actions = [
      "secretsmanager:GetSecretValue",
      "secretsmanager:DescribeSecret"
    ]
    effect = "Allow"
    resources = [
      "*"
    ]
  }

}

