resource "aws_cloudwatch_log_group" "lambda_log_group" {
  count = var.create_cloud_watch_log_group != null ? 1 : 0
  name  = var.cloud_watch_log_group
  lifecycle {
    ignore_changes  = [retention_in_days] # Ignore changes to retention policy
    prevent_destroy = true                # Prevent destruction of the log group
  }

}

resource "aws_iam_role" "lambda" {
  name                  = "${var.function_name}-iam-role"
  force_detach_policies = true
  max_session_duration  = 3600
  assume_role_policy = jsonencode({
    Version = "2012-10-17"
    Statement = [
      {
        Effect = "Allow"
        Principal = {
          Service = "lambda.amazonaws.com"
        }
        Action = "sts:AssumeRole"
      },
      {
        Effect = "Allow"
        Principal = {
          Service = "apigateway.amazonaws.com"
        }
        Action = "sts:AssumeRole"
      }
    ]
  })
}

resource "aws_iam_role_policy" "lambda" {
  count  = var.create_cloud_watch_log_group != null ? 1 : 0
  name   = "${var.function_name}-permissions"
  role   = aws_iam_role.lambda.name
  policy = data.aws_iam_policy_document.lambda[0].json
}

