resource "aws_security_group" "lambda_security_group" {
  count       = var.create_security_group != null ? 1 : 0
  name        = var.lambda_security_group_name
  description = "Security group for Lambda outbound traffic"
  vpc_id      = var.vpc_id
  dynamic "egress" {
    # for_each = var.egress_rules != null ? [var.egress_rules] : []
    for_each = var.egress_rules
    content {
      from_port       = egress.value.from_port
      to_port         = egress.value.to_port
      protocol        = egress.value.protocol
      security_groups = egress.value.security_groups
      cidr_blocks     = egress.value.cidr_blocks
    }
  }

  dynamic "ingress" {
    # for_each = var.ingress_rules != null ? [var.ingress_rules] : []
    for_each = var.ingress_rules
    content {
      from_port       = ingress.value.from_port
      to_port         = ingress.value.to_port
      protocol        = ingress.value.protocol
      security_groups = ingress.value.security_groups
      cidr_blocks     = ingress.value.cidr_blocks
    }
  }
  lifecycle {
    create_before_destroy = true
  }
}

