module "security_group" {
  source  = "terraform-aws-modules/security-group/aws"
  version = "~> 5.0"

  name        = local.name
  description = "Security group for ${local.name}"
  vpc_id      = data.aws_vpc.current.id

  egress_cidr_blocks = [ "0.0.0.0/0" ]
  egress_rules = [
    "all-all"
  ]

  ingress_cidr_blocks = local.sg_allowed_cidrs
  ingress_rules = [
    "kafka-broker-tcp",
    "kafka-broker-tls-tcp",
    "kafka-broker-sasl-iam-tcp"
  ]

  tags = local.tags
}


module "msk_cluster" {
  source = "git@github.com:omfterraform/terraform-aws-msk-serverless.git?ref=v1.1.1"

  cluster_name       = local.name
  subnet_ids         = local.private_subnet_ids
  security_group_ids = [module.security_group.security_group_id]
  create_s3_plugin   = 1
  aws_account_ids    = local.aws_account_ids
  vpc_id             = data.aws_vpc.current.id

  allow_assume = [
    "arn:aws:iam::636519986403:root", #Cards STG
    "arn:aws:iam::159211442627:root", #Svcshub STG
    "arn:aws:iam::014524682603:role/odop-cf-prod-jenkins-ue1-all-AgentIAMRole-1PFGKAX2ZB9FR", #Jenkins
    "arn:aws:iam::014524682603:role/odop-cf-prod-jenkins-ue1-all-MasterIAMRole-1VC99UC1OXJJZ" #Jenkins
  ]

  tags = local.tags
}

module "nlb_setup" {
  source             = "git@github.com:omfterraform/terraform-aws-msk-nlb.git?ref=v1.0.0"
  lb_name            = "oshs-ec2-stg-msk-nlb-ue1-all"
  security_groups    = [module.security_group.security_group_id]
  subnets            = local.private_subnet_ids
  vpc_id             = data.aws_vpc.current.id
  internal           = true
  tags               = {}
  vpce_ips           = local.vpce_ips
}

data "aws_vpc_endpoint" "msk" {
  vpc_id = data.aws_vpc.current.id
  tags = {
    ClusterArn	  = module.msk_cluster.arn
    AWSMSKManaged = "true"
  }
  depends_on = [ module.msk_cluster ]
}

data "aws_network_interface" "vpce" {
  for_each   = toset(data.aws_vpc_endpoint.msk.network_interface_ids)
  id         = each.key
  depends_on = [ module.msk_cluster, data.aws_vpc_endpoint.msk ]
}

locals {
  vpce_ips = flatten([
    for eni in data.aws_network_interface.vpce : [
      eni.private_ip
    ]
  ])
}

