resource "aws_cloudwatch_log_group" "lambda_log_group" {
  count = var.create_cloud_watch_log_group != null ? 1 : 0
  name  = var.cloud_watch_log_group
  lifecycle {
    ignore_changes  = [retention_in_days] # Ignore changes to retention policy
    prevent_destroy = true                # Prevent destruction of the log group
  }

}

resource "aws_iam_role" "lambda" {
  name                  = "${var.function_name}-iam-role"
  force_detach_policies = true
  max_session_duration  = 3600
  assume_role_policy = jsonencode({
    Version = "2012-10-17"
    Statement = [
      {
        Effect = "Allow"
        Principal = {
          Service = "lambda.amazonaws.com"
        }
        Action = "sts:AssumeRole"
      },
      {
        Effect = "Allow"
        Principal = {
          Service = "apigateway.amazonaws.com"
        }
        Action = "sts:AssumeRole"
      }
    ]
  })
}

resource "aws_iam_role_policy" "lambda" {
  count  = var.create_cloud_watch_log_group != null ? 1 : 0
  name   = "${var.function_name}-permissions"
  role   = aws_iam_role.lambda.name
  policy = data.aws_iam_policy_document.lambda[0].json
}

data "aws_caller_identity" "lambda" {}
data "aws_region" "lambda" {}

data "archive_file" "lambda_payload" {
  type       = "zip"
  source_dir = "${var.src}"
  output_path = var.zip_file_name
}


data "aws_iam_policy_document" "lambda" {
  count = var.create_cloud_watch_log_group != null ? 1 : 0
  statement {
    # sid = "writeLogs"
    actions = [
      "logs:CreateLogGroup",
      "logs:CreateLogStream",
      "logs:PutLogEvents",
    ]
    effect    = "Allow"
    resources = ["*"]
    # resources = [
    #   "${aws_cloudwatch_log_group.lambda_log_group[0].arn}:*"
    # ]
  }
  statement {
    actions = [
      "ec2:CreateNetworkInterface",
      "ec2:DescribeNetworkInterfaces",
      "ec2:DeleteNetworkInterface",
      "ec2:DetachNetworkInterface",
      "ec2:DescribeSecurityGroups", // Add this line
      "ec2:ModifyNetworkInterfaceAttribute"
    ]
    effect    = "Allow"
    resources = ["*"]
  }

  statement {
    actions = [
      "secretsmanager:GetSecretValue",
      "secretsmanager:DescribeSecret"
    ]
    effect = "Allow"
    resources = [
      "*"
    ]
  }

}

