          "secretsmanager:GetSecretValue",
          "secretsmanager:DescribeSecret"
        ],
        Resource = [
          "arn:aws:secretsmanager:${var.aws_region}:${var.aws_account_id}:secret:${var.env}/${var.project}/document-subgraph-shared",
          "arn:aws:secretsmanager:${var.aws_region}:${var.aws_account_id}:secret:${var.env}/${var.project}/document-subgraph-okta-api"
        ]
      },
      {
        Sid      = "AccessToRDSAuthToken"
        Effect   = "Allow"
        Action   = [
          "rds-db:connect"
        ],
        Resource = [
          "arn:aws:rds-db:${var.aws_region}:${var.aws_account_id}:dbuser:${var.database_rds_cluster_resource_id}/iam_postgres"
        ]
      }
    ]
  })
}

