resource "aws_security_group" "lambda_security_group" {
  count       = var.create_security_group != null ? 1 : 0
  name        = var.lambda_security_group_name
  description = "Security group for Lambda outbound traffic"
  vpc_id      = var.vpc_id
  dynamic "egress" {
    # for_each = var.egress_rules != null ? [var.egress_rules] : []
    for_each = var.egress_rules
    content {
      from_port       = egress.value.from_port
      to_port         = egress.value.to_port
      protocol        = egress.value.protocol
      security_groups = egress.value.security_groups
      cidr_blocks     = egress.value.cidr_blocks
    }
  }

  dynamic "ingress" {
    # for_each = var.ingress_rules != null ? [var.ingress_rules] : []
    for_each = var.ingress_rules
    content {
      from_port       = ingress.value.from_port
      to_port         = ingress.value.to_port
      protocol        = ingress.value.protocol
      security_groups = ingress.value.security_groups
      cidr_blocks     = ingress.value.cidr_blocks
    }
  }
  lifecycle {
    create_before_destroy = true
  }
}

resource "aws_vpc_endpoint" "api_gtwy_endpoint" {
  vpc_id              = data.terraform_remote_state.cluster_vpc.outputs.vpc_id
  service_name        = var.endpoint_execute_api_service_name
  subnet_ids          = local.selected_subnet_ids
  vpc_endpoint_type   = var.endpoint_type
  security_group_ids  = [aws_security_group.vpc_endpoint.id] 
  policy              = data.aws_iam_policy_document.vpc-endpoint-policy.json
  tags                = var.tags_execute_api_endpoint
}

resource "aws_security_group" "vpc_endpoint" {
  name        = "vpc-endpoint-execute-api-sg"
  description = "Security group for Execute API VPC Endpoint"
  vpc_id      = data.terraform_remote_state.cluster_vpc.outputs.vpc_id

  # Ingress rule for HTTPS from selected private subnets
  dynamic "ingress" {
    for_each = local.subnet_cidrs
    content {
      from_port   = 443
      to_port     = 443
      protocol    = "tcp"
      cidr_blocks = [ingress.value]
      description = "HTTPS from private subnet ${ingress.value}"
    }
  }

  # Egress rule - allow all outbound traffic
  egress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
    description = "Allow all outbound traffic"
  }

  tags = merge(
    var.tags_execute_api_endpoint,
    {
      Name = "vpc-endpoint-execute-api-sg"
    }
  )
}

